// Parameters
base_x = {base_x};
base_y = {base_y};
base_z = {base_z};
size = {size};

// Letter M (5x7 bitmap: 1=cube, 0=empty)
m_coords = [
    [0,0], [0,1], [1,2], [2,1], [2,0],
    [0,3], [0,4], [0,5], [0,6],
    [2,3], [2,4], [2,5], [2,6],
    [4,0], [4,1], [4,2], [4,3], [4,4], [4,5], [4,6]
];

// Letter C
c_coords = [
    [1,0], [2,0], [3,0],
    [0,1], [4,1],
    [0,2], [0,3], [0,4],
    [0,5], [4,5],
    [1,6], [2,6], [3,6]
];

// Letter P
p_coords = [
    [0,0], [1,0], [2,0], [3,0],
    [0,1], [4,1],
    [0,2], [4,2],
    [0,3], [1,3], [2,3], [3,3],
    [0,4], [0,5], [0,6]
];

// Convert coordinate pairs to Point objects
spacing = size * 1.5;
letter_spacing = size * 8;

// M letter points
m_pts = [Imperative] {
    pts = [];
    for (coord in m_coords) {
        x = base_x + coord[0] * spacing;
        y = base_y;
        z = base_z + (6 - coord[1]) * spacing;
        pts = List.AddItemToEnd(Point.ByCoordinates(x, y, z), pts);
    }
    return pts;
};

// C letter points
c_pts = [Imperative] {
    pts = [];
    for (coord in c_coords) {
        x = base_x + letter_spacing + coord[0] * spacing;
        y = base_y;
        z = base_z + (6 - coord[1]) * spacing;
        pts = List.AddItemToEnd(Point.ByCoordinates(x, y, z), pts);
    }
    return pts;
};

// P letter points
p_pts = [Imperative] {
    pts = [];
    for (coord in p_coords) {
        x = base_x + letter_spacing * 2 + coord[0] * spacing;
        y = base_y;
        z = base_z + (6 - coord[1]) * spacing;
        pts = List.AddItemToEnd(Point.ByCoordinates(x, y, z), pts);
    }
    return pts;
};

// Combine and create cubes
all_pts = List.Join([m_pts, c_pts, p_pts]);
cubes = Cuboid.ByLengths(all_pts, size, size, size);
